import React, { useState } from 'react';
import axios from 'axios';
import '../styles/AddTeacher.css'; // CSS file
import AdminSidebar from '../admin/AdminSidebar'; // Sidebar component
import AdminNavbar from '../admin/AdminNavbar'; // Navbar component

const AddTeacher = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    address: '',
    qualification: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/teacher/add', formData);
      alert('Teacher added successfully!');
      console.log(response.data);
      setFormData({ email: '', password: '', address: '', qualification: '' }); // Reset form
    } catch (error) {
      console.error('Error adding teacher:', error.response?.data || error.message);
      alert('Failed to add teacher. Please try again.');
    }
  };

  return (
    <div className="admin-layout">
      <AdminSidebar /> {/* Sidebar */}
      <div className="main-content">
        <AdminNavbar /> {/* Navbar */}
        <div className="add-teacher-container">
          <h2>Add Teacher</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="address"
              placeholder="Address"
              value={formData.address}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="qualification"
              placeholder="Qualification"
              value={formData.qualification}
              onChange={handleChange}
              required
            />
            <button type="submit">Add Teacher</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default AddTeacher;
