import React from 'react';
import { Navigate } from 'react-router-dom'; // Used for redirecting users
import { useAuth } from '../hooks/useAuth'; // Custom hook to check if the user is authenticated

// ProtectedRoute component that ensures the user is authenticated
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth(); // Get the authentication status from the custom hook

  // If not authenticated, redirect to login page
  if (!isAuthenticated) {
    return <Navigate to="/login" replace />;
  }

  // If authenticated, render the children components (i.e. protected routes)
  return children;
};

export default ProtectedRoute;
