import React from "react";
import { Link } from "react-router-dom";

const Sidebar = () => {
  const role = localStorage.getItem("role"); // Role from localStorage

  return (
    <div className="sidebar">
      {role === "admin" && (
        <>
          <Link to="/admin/dashboard">Admin Dashboard</Link>
          <Link to="/admin/add-teacher">Add Teacher</Link>
          <Link to="/admin/add-counselor">Add Counselor</Link>
          <Link to="/admin/history">History</Link>
        </>
      )}
      {role === "teacher" && (
        <>
          <Link to="/teacher/dashboard">Teacher Dashboard</Link>
        </>
      )}
      {role === "counselor" && (
        <>
          <Link to="/counselor/dashboard">Counselor Dashboard</Link>
        </>
      )}
    </div>
  );
};

export default Sidebar;
