import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Perform authentication logic here (e.g., make API call)
    const response = await fetch('your-backend-login-api', {
      method: 'POST',
      body: JSON.stringify({ email, password }),
    });

    const data = await response.json();
    if (data.success) {
      localStorage.setItem('isAuthenticated', true);
      localStorage.setItem('userRole', data.role); // Store user role (admin, teacher, counselor)
      
      if (data.role === 'admin') {
        navigate('/adminDashboard');
      } else if (data.role === 'teacher') {
        navigate('/teacherDashboard');
      } else if (data.role === 'counselor') {
        navigate('/counselorDashboard');
      }
    } else {
      // Handle login failure (e.g., show error message)
      alert('Login failed');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
