import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('admin'); // Default role is admin
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send login request to backend with email, password, and role
      const response = await axios.post('http://localhost:5000/api/login', {
        email,
        password,
        role
      });

      // Check if the response status is 200 (success)
      if (response.status === 200) {
        const { user, role } = response.data;

        // Store the user role and other details in localStorage
        localStorage.setItem('role', role);
        localStorage.setItem('userId', user.id);

        // Log the response for debugging
        console.log('Login response:', response.data);

        // Redirect based on the role
        if (role === 'admin') {
          navigate('/adminDashboard');
        } else if (role === 'teacher') {
          navigate('/teacherDashboard');
        } else if (role === 'counselor') {
          navigate('/counselorDashboard');
        }
      }
    } catch (error) {
      // Log error for debugging
      console.error('Login error:', error);

      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message); // Display error message
      } else {
        setErrorMessage('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Role</label>
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="admin">Admin</option>
            <option value="teacher">Teacher</option>
            <option value="counselor">Counselor</option>
          </select>
        </div>
        {errorMessage && <p>{errorMessage}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
