import React, { useState } from 'react';
import axios from '../../services/api';
import './styles/AddCounselor.css';

const AddCounselor = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [address, setAddress] = useState('');
    const [qualification, setQualification] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');

        const counselorData = { email, password, address, qualification };

        try {
            const response = await axios.post('/admin/add-counselor', counselorData);
            setMessage('Counselor added successfully!');
        } catch (err) {
            console.error(err);
            setMessage('Error adding counselor. Please try again.');
        }
    };

    return (
        <div className="add-counselor-container">
            <h2>Add Counselor</h2>
            {message && <p className="message">{message}</p>}
            <form onSubmit={handleSubmit}>
                <label>Email:</label>
                <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />

                <label>Password:</label>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />

                <label>Address:</label>
                <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                />

                <label>Qualification:</label>
                <input
                    type="text"
                    value={qualification}
                    onChange={(e) => setQualification(e.target.value)}
                    required
                />

                <button type="submit">Save Counselor</button>
            </form>
        </div>
    );
};

export default AddCounselor;
