import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate hook for redirection
import axios from 'axios';

const LoginPage = () => {
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('admin'); // Default role is admin
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Navigation hook

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send login request to the backend
      const response = await axios.post('http://localhost:5000/api/login', { id, password, role });

      if (response.status === 200) {
        const { role, user } = response.data;

        // Store the role and user info (if any) in localStorage
        localStorage.setItem('role', role);
        localStorage.setItem('userId', user.id); // Store user ID for later use if needed

        // Check the role and redirect accordingly
        if (role === 'admin') {
          navigate('/adminDashboard'); // Redirect to Admin Dashboard
        } else if (role === 'teacher') {
          navigate('/teacherDashboard'); // Redirect to Teacher Dashboard
        } else if (role === 'counselor') {
          navigate('/counselorDashboard'); // Redirect to Counselor Dashboard
        }
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message); // Display error message
      } else {
        setErrorMessage('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>ID</label>
          <input
            type="text"
            value={id}
            onChange={(e) => setId(e.target.value)} // Handle ID change
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Handle password change
            required
          />
        </div>
        <div>
          <label>Role</label>
          <select value={role} onChange={(e) => setRole(e.target.value)} required>
            <option value="admin">Admin</option>
            <option value="teacher">Teacher</option>
            <option value="counselor">Counselor</option>
          </select>
        </div>
        {errorMessage && <p>{errorMessage}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
