import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../services/api'; // Ensure your axios service is configured properly

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('admin'); // Default role
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // Call your API endpoint for login
      const response = await axios.post('/login', { email, password, role });
      const { token, role } = response.data;

      // Save token and role in localStorage
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);

      // Redirect based on role
      if (role === 'admin') navigate('/adminDashboard');
      else if (role === 'teacher') navigate('/teacherDashboard');
      else if (role === 'counselor') navigate('/counselorDashboard');
    } catch (err) {
      setError('Invalid login credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = () => {
    // Navigate to the Register page
    navigate('/register');
  };

  return (
    <div className="login-page">
      <h1>Login</h1>
      <form onSubmit={handleLogin} className="login-form">
        <label>
          Role:
          <select value={role} onChange={(e) => setRole(e.target.value)} disabled={loading}>
            <option value="admin">Admin</option>
            <option value="teacher">Teacher</option>
            <option value="counselor">Counselor</option>
          </select>
        </label>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
          />
        </label>
        {error && <p className="error">{error}</p>}
        <div className="login-actions">
          <button type="submit" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
          <button type="button" onClick={handleRegister} disabled={loading}>
            Register
          </button>
        </div>
      </form>
    </div>
  );
};

export default LoginPage;
