import React from "react";
import { Link } from "react-router-dom";
import "./styles/Sidebar.css"; // Link to the Sidebar-specific styles

const Sidebar = () => {
  const role = localStorage.getItem("role");

  return (
    <div className="sidebar">
      <ul>
        {role === "admin" && (
          <>
            <li>
              <Link to="/admin/add-teacher">Add Teacher</Link>
            </li>
            <li>
              <Link to="/admin/add-counselor">Add Counselor</Link>
            </li>
            <li>
              <Link to="/admin/history">History</Link>
            </li>
          </>
        )}
        {role === "teacher" && (
          <>
            <li>
              <Link to="/teacher/dashboard">Teacher Dashboard</Link>
            </li>
          </>
        )}
        {role === "counselor" && (
          <>
            <li>
              <Link to="/counselor/dashboard">Counselor Dashboard</Link>
            </li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
