import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
      {/* Logo */}
      <h1 className="text-xl font-bold">Army Boys Hostel</h1>

      {/* Mobile Menu Button */}
      <div className="md:hidden text-2xl cursor-pointer" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </div>

      {/* Navbar Links */}
      <ul className={`md:flex space-x-4 absolute md:static bg-gray-800 w-full left-0 top-14 md:top-auto md:w-auto transition-transform duration-300 ${
        isOpen ? "translate-x-0" : "-translate-x-full md:translate-x-0"
      } md:flex-row flex-col`}>
        <li className="p-3 md:p-0"><Link to="/">Home</Link></li>
        <li className="p-3 md:p-0"><Link to="/events">Events</Link></li>
        <li className="p-3 md:p-0"><Link to="/contact">Contact</Link></li>
        <li className="p-3 md:p-0"><Link to="/login">Login</Link></li>
      </ul>
    </nav>
  );
};

export default Navbar;
