import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from '../pages/LoginPage';
import AdminDashboard from '../pages/Admin/AdminDashboard';
import RegisterAdmin from '../pages/Admin/RegisterAdmin';
import AddTeacher from '../pages/Admin/AddTeacher';
import AddCounselor from '../pages/Admin/AddCounselor';
import History from '../pages/Admin/History';
import TeacherDashboard from '../pages/Teacher/TeacherDashboard';
import CounselorDashboard from '../pages/Counselor/CounselorDashboard';

// Component for protected routes based on authentication and roles
const ProtectedRoute = ({ children, allowedRoles }) => {
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');

  if (!token) {
    // If no token, redirect to login
    return <Navigate to="/login" />;
  }

  if (allowedRoles && !allowedRoles.includes(role)) {
    // If role is not allowed, redirect to login
    return <Navigate to="/login" />;
  }

  return children;
};

const AppRoutes = () => {
  return (
    <Routes>
      {/* Redirect "/" to "/login" */}
      <Route path="/" element={<Navigate to="/login" />} />

      {/* Public Route: Login */}
      <Route path="/login" element={<LoginPage />} />

      {/* Public Route: Register */}
      <Route path="/register" element={<RegisterAdmin />} />

      {/* Protected Routes: Admin */}
      <Route
        path="/adminDashboard"
        element={
          <ProtectedRoute allowedRoles={['admin']}>
            <AdminDashboard />
          </ProtectedRoute>
        }
      />
      <Route
        path="/addTeacher"
        element={
          <ProtectedRoute allowedRoles={['admin']}>
            <AddTeacher />
          </ProtectedRoute>
        }
      />
      <Route
        path="/addCounselor"
        element={
          <ProtectedRoute allowedRoles={['admin']}>
            <AddCounselor />
          </ProtectedRoute>
        }
      />
      <Route
        path="/history"
        element={
          <ProtectedRoute allowedRoles={['admin']}>
            <History />
          </ProtectedRoute>
        }
      />

      {/* Protected Routes: Teacher */}
      <Route
        path="/teacherDashboard"
        element={
          <ProtectedRoute allowedRoles={['teacher']}>
            <TeacherDashboard />
          </ProtectedRoute>
        }
      />

      {/* Protected Routes: Counselor */}
      <Route
        path="/counselorDashboard"
        element={
          <ProtectedRoute allowedRoles={['counselor']}>
            <CounselorDashboard />
          </ProtectedRoute>
        }
      />

      {/* Fallback for Undefined Routes */}
      <Route path="*" element={<Navigate to="/login" />} />
    </Routes>
  );
};

export default AppRoutes;
