import React from 'react';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const role = localStorage.getItem('role'); // Get role from localStorage

  return (
    <div className="sidebar">
      {role === 'admin' && (
        <>
          <Link to="/add-teacher">Add Teacher</Link>
          <Link to="/add-counselor">Add Counselor</Link>
          <Link to="/history">History</Link>
        </>
      )}
      {role === 'teacher' && (
        <>
          <Link to="/teacherDashboard">Teacher Dashboard</Link>
          <Link to="/history">Teacher History</Link>
        </>
      )}
      {role === 'counselor' && (
        <>
          <Link to="/counselorDashboard">Counselor Dashboard</Link>
          <Link to="/history">Counselor History</Link>
        </>
      )}
    </div>
  );
};

export default Sidebar;
