import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate hook for redirection
import axios from 'axios';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // To navigate to another page

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Send login request to the backend
      const response = await axios.post('http://localhost:5000/api/login', { email, password });

      if (response.status === 200) {
        // Assuming the backend sends the user's role along with the response
        const { role } = response.data;

        // Store the role and token (if any) in localStorage or sessionStorage
        localStorage.setItem('role', role);
        localStorage.setItem('token', response.data.token); // If you're using a token for authentication

        // Check the role and redirect accordingly
        if (role === 'admin') {
          // Admin role redirects to Admin Dashboard (with path pointing to admin folder)
          navigate('/admin/adminDashboard');
        } else {
          // Redirect to a regular user page or homepage
          navigate('/home');
        }
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setErrorMessage(error.response.data.message);
      } else {
        setErrorMessage('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {errorMessage && <p>{errorMessage}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default LoginPage;
