import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api', // Make sure this is the correct API URL
});

// Interceptor to add token to the request header
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token'); // or wherever you store your token
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`; // Attach token
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Example of making an API request to add a teacher
const addTeacher = async (teacherData) => {
  try {
    const response = await api.post('/teacher/add', teacherData);
    console.log('Teacher added:', response.data);
  } catch (error) {
    console.error('Error adding teacher:', error.response || error);
  }
};
