import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const role = localStorage.getItem("role");

  return (
    <>
      {/* Mobile Sidebar Toggle */}
      <div
        className="fixed top-4 left-4 z-50 text-white text-2xl cursor-pointer md:hidden"
        onClick={() => setIsOpen(!isOpen)}
      >
        {isOpen ? <FaTimes /> : <FaBars />}
      </div>

      {/* Sidebar */}
      <div
        className={`fixed top-16 left-0 h-full w-64 bg-gray-900 text-white transition-transform duration-300 md:translate-x-0 ${
          isOpen ? "translate-x-0" : "-translate-x-full"
        } md:flex md:flex-col md:w-64`}
      >
        <h2 className="text-xl font-bold p-4">Dashboard</h2>
        <nav className="flex flex-col space-y-2 overflow-y-auto max-h-[80vh]">
          {role === "admin" && (
            <>
              <Link className="p-3 hover:bg-gray-700" to="/admin/dashboard">
                Admin Dashboard
              </Link>
              <Link className="p-3 hover:bg-gray-700" to="/admin/add-teacher">
                Add Teacher
              </Link>
              <Link className="p-3 hover:bg-gray-700" to="/admin/add-counselor">
                Add Counselor
              </Link>
              <Link className="p-3 hover:bg-gray-700" to="/admin/history">
                History
              </Link>
            </>
          )}
          {role === "teacher" && (
            <Link className="p-3 hover:bg-gray-700" to="/teacher/dashboard">
              Teacher Dashboard
            </Link>
          )}
          {role === "counselor" && (
            <Link className="p-3 hover:bg-gray-700" to="/counselor/dashboard">
              Counselor Dashboard
            </Link>
          )}
        </nav>
      </div>
    </>
  );
};

export default Sidebar;
